  `id` int(11) NOT NULL AUTO_INCREMENT,
  `v_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `name` varchar(100) NOT NULL,
  `text` text NOT NULL,
  `education` varchar(60) NOT NULL COMMENT 'образование',
  `conditions` text NOT NULL COMMENT 'условия работы',
  `duties` text NOT NULL COMMENT 'обязанности',
  `experience` varchar(60) NOT NULL COMMENT 'опыт работы',
  `pay` varchar(50) NOT NULL,
  `employment` varchar(30) NOT NULL COMMENT 'занятость',
  `more` text NOT NULL COMMENT 'дополнительная информация',
  `contactname` text NOT NULL COMMENT 'контактное лицо',
  `contactphone` varchar(30) NOT NULL COMMENT 'контактный телефон',
  `contactmail` varchar(40) NOT NULL COMMENT 'почта',
  `visible` tinyint(1) NOT NULL DEFAULT '0',
  `sort` int(11) NOT NULL DEFAULT '1000',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=5 ;

