  `v_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `name` varchar(100) NOT NULL,
  `text` text NOT NULL,
  `education` varchar(60) NOT NULL COMMENT 'образование',
  `conditions` text NOT NULL COMMENT 'условия работы',
  `duties` text NOT NULL COMMENT 'обязанности',
  `experience` varchar(60) NOT NULL COMMENT 'опыт работы',
  `pay` varchar(50) NOT NULL,
